mapper.select:
SELECT 
	ADMIN_NO
FROM ADMIN
WHERE ID=#{id}
AND PWD = #{pwd}

SELECT * FROM ( SELECT ROWNUM RNUM, T.* FROM ( SELECT V.VISIT_NO ,V.MEMBER_NO ,V.RESERVATION_STATUS_NO ,V.RESERVATION_DATE ,V.SUMMIT_DATE ,V.MODIFY_DATE ,V.DEL_YN ,S.RESERVATION_STATUS ,M.NAME ,M.PHONE_NUMBER FROM VISIT_RESERVATION V JOIN RESERVATION_STATUS S ON S.RESERVATION_STATUS_NO = V.RESERVATION_STATUS_NO JOIN MEMBER M ON M.MEMBER_NO = V.MEMBER_NO
S.RESERVATION_STATUS IN ('예약 중', '상담 완료', '예약 취소')
AND S.RESERVATION_STATUS = #{reservationStatus}
ORDER BY
V.VISIT_NO DESC V.RESERVATION_DATE ASC
) T ) WHERE RNUM BETWEEN #{startRow} AND #{lastRow}
