mapper.select:
SELECT ADOPTION_NO, IMAGE_NO, RESCUE_DOG_NO, ADMIN_NO, ENROLL_DATE, MODIFY_DATE, DEL_YN
FROM ADOPTION_BOARD
WHERE ADOPTION_NO = #{adoptionNo}

mapper.insert:
INSERT INTO ADOPTION_BOARD 
(
	ADOPTION_NO
	, IMAGE_NO
	, RESCUE_DOG_NO
	, ADMIN_NO
	, ENROLL_DATE
	, MODIFY_DATE
	, DEL_YN
)
VALUES 
(
	SEQ_ADOPTION_NO.NEXTVAL
	, #{imageNo}
	, #{rescueDogNo}
	, #{adminNo}
	, #{enrollDate}
	, #{modifyDate}
	, #{delYn}
)

mapper.update:
UPDATE ADOPTION_BOARD
SET DEL_YN = 'Y'
WHERE ADOPTION_NO = #{adoptionNo}
