mapper.insert:
		INSERT INTO ADOPTION_BOARD
        (
            ADOPTION_BOARD_NO
            , IMAGE_NO
            , RESCUE_DOG_NO
            , ADMIN_NO
        )
        VALUES
        (
            SEQ_ADOPTION_BOARD_NO.NEXTVAL
            , #{imageNo}
            , #{rescueDogNo}
            , #{adminNo}
        )

    	INSERT INTO IMAGE
		(
			IMAGE_NO
			,IMAGE_PATH
		)
		VALUES
		(
			SEQ_IMAGE_NO.NEXTVAL
			,#{imagePath}
		)

mapper.update:
UPDATE
	IMAGE
SET
	IMAGE_PATH = #{imagePath}
WHERE
	IMAGE_NO = #{imageNo}

mapper.insert:
    	INSERT INTO RESCUE_DOG
		(
			RESCUE_DOG_NO
			, IMAGE_NO
			, BREED
			, ADMISSION_DATE
			, DOG_NAME
			, AGE
			, GENDER_MF
			, NEUTERING_OX
			, WEIGHT
		)
		VALUES
		(
			SEQ_RESCUE_DOG_NO.NEXTVAL
			, #{imageNo}
			, #{breed}
			, SYSDATE
			, #{dogName}
			, #{age}
			, #{genderMf}
			, #{neuteringOx}
			, #{weight}
		)
