mapper.insert:
		INSERT INTO NEWS_AFTER_ADOPTION
        (
            NEWS_AFTER_ADOPTION_NO
            , MEMBER_NO
            , IMAGE_NO
            , TITLE
            , CONTENT
        )
        VALUES
        (
            SEQ_NEWS_AFTER_ADOPTION_NO.NEXTVAL
            , #{memberNo}
            , #{imageNo}
            , #{title}
            , #{content}
        )

INSERT INTO IMAGE
(
	IMAGE_NO
	,IMAGE_PATH
)
VALUES
(
	SEQ_IMAGE_NO.NEXTVAL
	,#{imagePath}
)

mapper.update:
	UPDATE 
		NEWS_AFTER_ADOPTION
    SET 
        TITLE = #{title}
        , CONTENT = #{content}
        , MODIFY_DATE = SYSDATE
    WHERE 
    	NEWS_AFTER_ADOPTION_NO = #{newsAfterAdoptionNo}
    	AND DEL_YN = 'N'

	UPDATE 
		IMAGE
	SET
		IMAGE_PATH = #{imagePath}
    WHERE
        IMAGE_NO = #{imageNo}
