mapper.insert:
  		INSERT INTO APPLY
  		(
  			APPLY_NO
			, MEMBER_NO
			, RESCUE_DOG_NO
			, DECISION_TIME
			, MAIN_REASON
			, FAMILY_MEMBERS
			, CURRENT_PETS
			, LANDLORD_PERMISSION_YN
			, CONFLICTRESOLUTION
  		)
  		VALUES
  		(
  			SEQ_APPLY_NO.NEXTVAL
  			, #{memberNo}
  			, #{rescueDogNo}
  			, #{decisionTime}
  			, #{mainReason}
  			, #{familyMembers}
  			, #{currentPets}
  			, #{landlordPermissionYN}
  			, #{conflictResolution}
  		)

mapper.update:
UPDATE APPLY 
SET

    DECISION_TIME = #{decisionTime},


    MAIN_REASON = #{mainReason},


    FAMILY_MEMBERS = #{familyMembers},


    CURRENT_PETS = #{currentPets},


    LANDLORD_PERMISSION_YN = #{landlordPermissionYn},


    CONFLICTRESOLUTION = #{conflictResolution}

WHERE MEMBER_NO = #{memberNo} 
AND RESCUE_DOG_NO = #{rescueDogNo}
