mapper.insert:
  		INSERT INTO MEMBER
  		(
  			MEMBER_NO
  			, ID
  			, PWD
  			, NICK
  			, NAME
  			, PHONE_NUMBER
  			, EMAIL
  			, BIRTHDAY
  		) 
  		VALUES
  		(
	  		SEQ_MEMBER_NO.NEXTVAL
	  		, #{id}
	  		, #{pwd}
	  		, #{nick}
	  		, #{name}
	  		, #{phoneNumber}
	  		, #{email}
	  		, #{birthday}
  		)

mapper.update:
UPDATE MEMBER 
	SET 
	PWD = #{pwd}


	, NICK = #{nick}


	, NAME = #{name}


	, PHONE_NUMBER = #{phoneNumber}


	, EMAIL = #{email}


	, BIRTHDAY = #{birthday}


	 WHERE NO = #{memberNo} 
