mapper.insert:
  		INSERT INTO MEMBER
  		(
  			MEMBER_NO
  			, ID
  			, PWD
  			, NICK
  			, NAME
  			, PHONE_NUMBER
  			, EMAIL
  			, BIRTHDAY
  		) 
  		VALUES
  		(
	  		SEQ_MEMBER_NO.NEXTVAL
	  		, #{id}
	  		, #{pwd}
	  		, #{nick}
	  		, #{name}
	  		, #{phoneNumber}
	  		, #{email}
	  		, #{birthday}
  		)

mapper.select:
SELECT ID
FROM MEMBER
WHERE NAME=#{name}
AND EMAIL=#{email}

mapper.update:
UPDATE MEMBER SET
	PWD = #{pwd}
WHERE ID = #{id}
AND NAME = #{name}
AND EMAIL = #{email}
AND DEL_YN = 'N'
