mapper.insert:
INSERT INTO REPORT_BULLETIN_BOARD
(
	REPORT_NO
	,MEMBER_NO
	,IMAGE_NO
	,TITLE
	,CONTENT
) 
VALUES
(
	SEQ_REPORT_BULLETIN_BOARD_NO.NEXTVAL, 
	#{memberNo}
	,#{imageNo}
	,#{title}
	,#{content}
)

INSERT INTO IMAGE
(
	IMAGE_NO
	,IMAGE_PATH
)
VALUES
(
	SEQ_IMAGE_NO.NEXTVAL
	,#{imagePath}
)

mapper.update:
	UPDATE 
		REPORT_BULLETIN_BOARD
    SET
        TITLE = #{title}
        ,CONTENT = #{content}
        ,MODIFY_DATE = SYSDATE
    WHERE
        REPORT_NO = #{reportNo}
        AND DEL_YN = 'N'

	UPDATE
		IMAGE
    SET
		IMAGE_PATH = #{imagePath}
    WHERE
        IMAGE_NO = #{imageNo}
